import React, { Suspense, useEffect, useState } from 'react';
import camiloNodders from './camiloczNodders.gif';
import camiloPillar from './camiloczPillar.gif';
import markyJam from './markynJam.gif';
import './App.css';
import axios from 'axios';
import { Streams } from './Components/Streams';




const gifEmotes = [
  {
    src: camiloNodders
  },
  {
    src: camiloPillar
  },
  {
    src: markyJam
  }
];


function App() {

  const [randomGif, setRandomGif] = useState('');
  const [loading, setLoadingStatus] = useState(true);
  const [communityStreams, setStreams] = useState([]);
  const [thumbnailChange, setThumbnailChange] = useState('');

  useEffect(() => {
    const fetchStreams = async () => {
      axios.get('http://localhost:8080/twitch').then((res) => {
        setStreams(res.data);
        setThumbnailChange(res.data[0].thumbnail_url);
        setTimeout(() => setLoadingStatus(false), 480);
      });
    };
    fetchStreams();
  }, []);

  useEffect(() => {
    const randomGif = gifEmotes[Math.floor( Math.random() * gifEmotes.length )];
    setRandomGif(randomGif.src);
  }, [gifEmotes]);
  return (
    <div className="App">
      <div className='w-screen h-auto bg-[#141b26] sticky'>
        <div className='flex mx-auto justify-center md:mr-24 mr-10'>
          <img src={randomGif} className='flex w-20 h-20'/>
          <a className='flex text-gray-200 text-4xl font-bold italic ml-2 mt-[1.9rem] hover:underline hover:underline-offset-8 cursor-pointer' href={'/'}>Da Community</a>
        </div>
      </div>
        <div className='align-bottom w-full h-12 bg-[#141b26] fixed justify-center bottom-0'>
            <div className='flex justify-center mt-[1rem]'>
              <a className='text-gray-200 text-sm mr-4 hover:underline hover:underline-offset-2'>Emote Suggestions</a>
              <a className='text-gray-200 text-sm hover:underline hover:underline-offset-2 cursor-pointer'>Privacy Policy</a>
              <a className='text-gray-200 text-sm ml-4 hover:underline hover:underline-offset-2 cursor-pointer'>Terms of Service</a>
            </div>
        </div>
    </div>
  );
}

export default App;
