import React, { useEffect, useState } from 'react';
import camiloNodders from './camiloczNodders.gif';
import camiloPillar from './camiloczPillar.gif';
import markyJam from './markynJam.gif';
import './App.css';
import axios from 'axios';


interface Streamers {
  id: string;
  streamer: string;
  title: string;
  thumbnail_url: string;
  viewers: number;
}

const streamers = [
  {
    id: 1,
    streamer: "Chenny323",
    title: "HIMOTHY IS REACTINGasdasdasdasdasdasd!!",
    thumbnail_url: "https://i.imgur.com/02Qh5Y9.jpg",
    profilepic_url: "https://i.imgur.com/qRnVmA8.png",
    viewers: 1000,
  },
  {
    id: 2,
    streamer: "SweaterGxd",
    title: "QUICK IRL WITH THE GANG!",
    thumbnail_url: "https://i.imgur.com/02Qh5Y9.jpg",
    profilepic_url: "https://i.imgur.com/qRnVmA8.png",
    viewers: 600
  },
  {
    id: 3,
    streamer: "MarkyNextDoor",
    title: "Late Night Vibes get in here",
    thumbnail_url: "https://i.imgur.com/02Qh5Y9.jpg",
    profilepic_url: "https://i.imgur.com/qRnVmA8.png",
    viewers: 500
  },
  {
    id: 4,
    streamer: "Kobe0802",
    title: "Playing games and chilling",
    thumbnail_url: "https://i.imgur.com/02Qh5Y9.jpg",
    profilepic_url: "https://i.imgur.com/qRnVmA8.png",
    viewers: 400
  },
  {
    id: 5,
    streamer: "Camilo",
    title: "Casamilo in the building!!",
    thumbnail_url: "https://i.imgur.com/02Qh5Y9.jpg",
    profilepic_url: "https://i.imgur.com/qRnVmA8.png",
    viewers: 300
  },
  {
    id: 6,
    streamer: "Chrxles",
    title: "Chrxles in the building!!",
    thumbnail_url: "https://i.imgur.com/02Qh5Y9.jpg",
    profilepic_url: "https://i.imgur.com/qRnVmA8.png",
    viewers: 275
  },
  {
    id: 7,
    streamer: "Flax",
    title: "Day 8.5 New Kendrick with the boys",
    thumbnail_url: "https://i.imgur.com/02Qh5Y9.jpg",
    profilepic_url: "https://i.imgur.com/qRnVmA8.png",
    viewers: 250
  },
  {
    id: 8,
    streamer: "Razo",
    title: "IRL GYM STREAM w/ DA IRL HOMIES",
    thumbnail_url: "https://i.imgur.com/02Qh5Y9.jpg",
    profilepic_url: "https://i.imgur.com/qRnVmA8.png",
    viewers: 200
  }
];

const gifEmotes = [
  {
    src: camiloNodders
  },
  {
    src: camiloPillar
  },
  {
    src: markyJam
  }
];


function App() {

  const [randomGif, setRandomGif] = useState('');
  const [streams, setStreams] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:8080/twitch').then((res) => {
      setStreams(res.data);
    });
  });

  useEffect(() => {
    const randomGif = gifEmotes[Math.floor( Math.random() * gifEmotes.length )];
    setRandomGif(randomGif.src);
  });
  return (
    <div className="App">
      <div className='w-screen h-auto bg-[#141b26] sticky'>
        <div className='flex mx-auto justify-center md:mr-24 mr-10'>
          <img src={randomGif} className='flex w-20 h-20'/>
          <a className='flex text-gray-200 text-4xl font-bold italic ml-2 mt-[1.8rem] hover:underline hover:underline-offset-8 cursor-pointer' href={'/'}>Da Community</a>
        </div>
      </div>
      <div className='container flex md:mx-auto mr-48 px-46 justify-center align-middle items-center'>
        <section className='py-8 px-6'>
          <div className='flex flex-wrap -mb-8 mx-[13rem]'>
            {streams.map((streamers : Streamers) =>
              <div key={streamers.id}>
                <div className='group' onClick={() => window.open(`https://twitch.tv/${streamers.streamer}`)}>
                  <div className='px-4 mb-8 truncate card-zoom cursor-pointer'>
                    <img className='card-zoom-image group-hover:card-zoom-image' src={streamers.thumbnail_url.replace('{width}', '275').replace('{height}', '275')} width={275} height={275}/>
                    <div className='absolute w-auto h-auto bg-gray-900/50 px-2 align-bottom mt-[6.5rem] ml-24 rounded-md'>
                      <a className='text-gray-200 opacity-100'>{streamers.viewers} viewers</a>
                    </div>
                  </div>
                  <div className='flex-1 w-60 h-auto'>
                      <div className='text-left overflow-hidden truncate w-60 ml-3 hover:cursor-pointer group-hover:text-purple-600 group-hover:transition group-hover:duration-[650ms] group-hover:ease-in-out'>{streamers.title}</div><br></br>
                      <div className='text-left -mt-6 ml-10'>{streamers.streamer}</div>
                    </div>
                </div>
              </div>
            )}
          </div>
        </section>
      </div>
    </div>
  );
}

export default App;
