import React, { Suspense, useEffect, useState } from 'react';
import { User }  from 'phosphor-react';
import camiloNodders from './camiloczNodders.gif';
import camiloPillar from './camiloczPillar.gif';
import markyJam from './markynJam.gif';
import { fetchCommunityStreams } from './api/api';
import './App.css';
import axios from 'axios';


interface Streamer {
  id: string;
  user_name: string;
  title: string;
  thumbnail_url: string;
  viewer_count: number;
}


const gifEmotes = [
  {
    src: camiloNodders
  },
  {
    src: camiloPillar
  },
  {
    src: markyJam
  }
];


function App() {

  const [randomGif, setRandomGif] = useState('');
  const [loading, setLoadingStatus] = useState(true);
  const streams = fetchCommunityStreams();
  const [thumbnailChange, setThumbnailChange] = useState('');

  useEffect(() => {
  }, [streams || thumbnailChange]);

  useEffect(() => {
    const randomGif = gifEmotes[Math.floor( Math.random() * gifEmotes.length )];
    setRandomGif(randomGif.src);
  }, [gifEmotes]);
  const streamss = streams.streams.read();
  return (
    <div className="App">
      <div className='w-screen h-auto bg-[#141b26] sticky'>
        <div className='flex mx-auto justify-center md:mr-24 mr-10'>
          <img src={randomGif} className='flex w-20 h-20'/>
          <a className='flex text-gray-200 text-4xl font-bold italic ml-2 mt-[1.5rem] hover:underline hover:underline-offset-8 cursor-pointer' href={'/'}>Da Community</a>
        </div>
      </div>
      <div className='container flex md:mx-auto mr-48 px-46 justify-center align-middle items-center'>
        <section className='py-8 px-6'>
          <div className='flex flex-wrap -mb-8 mx-[13rem]'>
            <Suspense fallback={<h1>Loading...</h1>}>
              {streamss.map((streamer : Streamer, index : number) =>
                <div key={streamer.id}>
                  <div className='group' onClick={() => window.open(`https://twitch.tv/${streamer.user_name}`)}>
                    <div className='px-4 mb-8 truncate card-zoom cursor-pointer'>
                      <img className='card-zoom-image' src={streamer.thumbnail_url.replace('{width}', '275').replace('{height}', '275')} width={275} height={275} key={Date.now() + index}/>
                      <div className='absolute w-auto h-auto bg-gray-900/50 px-2 align-bottom mt-[6.5rem] ml-24 rounded-md'>
                        <a className='text-gray-200 opacity-100'>{streamer.viewer_count.toLocaleString("en-US")} viewers</a>
                      </div>
                    </div>
                    <div className='flex-1 w-60 h-auto'>
                        <div className='text-left overflow-hidden truncate w-60 ml-3 hover:cursor-pointer group-hover:text-purple-600 group-hover:transition group-hover:duration-[650ms] group-hover:ease-in-out'>{streamer.title}</div><br></br>
                        <User size={26} className='-mt-4 ml-2' color='red'/>
                        <div className='text-left -mt-[1.5rem] ml-9 text-md'>{streamer.user_name}</div>
                      </div>
                  </div>
                </div>
              )}
            </Suspense>
          </div>
        </section>
      </div>
        <div className='align-bottom w-full h-12 bg-[#141b26] fixed bottom-0'>
            <div className='flex justify-center mt-[0.60rem]'>
              <a className='text-gray-200 text-sm mr-4'>Emote Suggestions</a>
              <a className='text-gray-200 text-sm'>Privacy Policy</a>
              <a className='text-gray-200 text-sm ml-4'>Terms of Service</a>
            </div>
        </div>
    </div>
  );
}

export default App;
