import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import { useNavigate } from 'react-router';
import { Navigate } from 'react-router';
import './App.css';

const streamers = [
  {
    id: 1,
    streamer: "Chenny323",
    title: "HIMOTHY IS REACTINGasdasdasdasdasdasd!!",
    thumbnail_url: "https://i.imgur.com/02Qh5Y9.jpg",
    profilepic_url: "https://i.imgur.com/qRnVmA8.png",
    viewers: 1000,
  },
  {
    id: 2,
    streamer: "SweaterGxd",
    title: "QUICK IRL WITH THE GANG!",
    thumbnail_url: "https://i.imgur.com/02Qh5Y9.jpg",
    profilepic_url: "https://i.imgur.com/qRnVmA8.png",
    viewers: 600
  },
  {
    id: 3,
    streamer: "MarkyNextDoor",
    title: "Late Night Vibes get in here",
    thumbnail_url: "https://i.imgur.com/02Qh5Y9.jpg",
    profilepic_url: "https://i.imgur.com/qRnVmA8.png",
    viewers: 500
  },
  {
    id: 4,
    streamer: "Kobe0802",
    title: "Playing games and chilling",
    thumbnail_url: "https://i.imgur.com/02Qh5Y9.jpg",
    profilepic_url: "https://i.imgur.com/qRnVmA8.png",
    viewers: 400
  },
  {
    id: 5,
    streamer: "Camilo",
    title: "Casamilo in the building!!",
    thumbnail_url: "https://i.imgur.com/02Qh5Y9.jpg",
    profilepic_url: "https://i.imgur.com/qRnVmA8.png",
    viewers: 300
  },
  {
    id: 6,
    streamer: "Flax",
    title: "Day 8.5 New Kendrick with the boys",
    thumbnail_url: "https://i.imgur.com/02Qh5Y9.jpg",
    profilepic_url: "https://i.imgur.com/qRnVmA8.png",
    viewers: 250
  },
  {
    id: 7,
    streamer: "Razo",
    title: "IRL GYM STREAM w/ DA IRL HOMIES",
    thumbnail_url: "https://i.imgur.com/02Qh5Y9.jpg",
    profilepic_url: "https://i.imgur.com/qRnVmA8.png",
    viewers: 200
  }
];

const gifEmotes = [
  {
    src: require('./camiloczNodders.gif')
  },
  {
    src: require('./camiloczPillar.gif')
  },
  {
    src: require('./markynJam.gif')
  }
];


function App() {

  const [randomGif, setRandomGif] = useState('');
  const [homeClicked, setHomeClicked] = useState(false);

  const onHomeClicked = () => {

  };

  useEffect(() => {
    const randomGif = gifEmotes[Math.floor( Math.random() * gifEmotes.length )];
    setRandomGif(randomGif.src);
  });
  return (
    <div className="App">
      <div className='w-screen h-auto bg-[#141b26] sticky'>
        <div className='flex mx-auto justify-center md:mr-24 mr-10'>
          <img src={randomGif} className='flex w-20 h-20'/>
          <a className='flex text-gray-200 text-4xl font-bold italic ml-2 mt-[1.8rem] hover:underline hover:underline-offset-8 cursor-pointer' ref='http://localhost:3000'>Da Community</a>
        </div>
      </div>
      <div className='container flex md:mx-auto mr-42 px-46 justify-center align-middle items-center'>
        <section className='py-8 px-4'>
          <div className='flex flex-wrap -mb-8 mx-[5rem]'>
            {streamers.map((streamers) =>
              <div key={streamers.id}>
                <div className='group' onClick={() => window.open(`https://twitch.tv/${streamers.streamer}`)}>
                  <div className='px-4 mb-8 truncate card-zoom cursor-pointer'>
                    <img className='card-zoom-image group-hover:card-zoom-image' src={streamers.thumbnail_url} width={275} height={275}/>
                    <div className='absolute w-auto h-auto bg-gray-900/50 px-2 align-bottom mt-[6.5rem] ml-24 rounded-md'>
                      <a className='text-gray-200 opacity-100'>{streamers.viewers} viewers</a>
                    </div>
                  </div>
                  <div className='flex-1 w-60 h-auto'>
                      <div className='text-left overflow-hidden truncate w-60 ml-3 hover:cursor-pointer group-hover:text-purple-600 group-hover:transition group-hover:duration-[650ms] group-hover:ease-in-out'>{streamers.title}</div><br></br>
                      <img className='rounded-full px-auto mr-1 -mt-4 ml-3' src={streamers.profilepic_url} width={25} height={25}/>
                      <div className='text-left -mt-6 ml-10'>{streamers.streamer}</div>
                    </div>
                </div>
              </div>
            )}
          </div>
        </section>
      </div>
    </div>
  );
}

export default App;
